uint16_t unpack(const uint8_t* data) {
	uint16_t result = 0;
	result |= static_cast<uint16_t>(data[1]) << 8;  //大端在后
	result |= data[0];
	return result;
}

void ReadRawFromFile(const char* filePath, uint16_t* bTemp)
{
	FILE* fp;
	fp = fopen(filePath, "rb");
	if (fp == NULL)
	{
		std::cout << "打开文件失败";
		return;
	}
	fpos_t startpos, endpos;
	fseek(fp, 0, SEEK_END);
	fgetpos(fp, &endpos);
	fseek(fp, 0, SEEK_SET);
	fgetpos(fp, &startpos);
	long file1en = (long)(endpos - startpos);
	uint8_t* data = new uint8_t[file1en];

	if (bTemp == NULL)
	{
		fclose(fp);
		return;
	}
	//memset(bTemp, 0, file1en);
	fread(reinterpret_cast<char*>(data), sizeof(char), file1en, fp);
	fclose(fp);

	// 遍历每个数据项，并调用readInt10函数将其解析为10位数据  
	for (size_t i = 0; i < file1en; i += 2) {
		uint16_t value = unpack(&data[i]);
		*(bTemp + i / 2) = value;
		//std::cout << "Value at offset " << i << ": " << value << std::endl;
	}

	// 释放内存并删除指针  
	delete[] data;
	data = nullptr;
	return;
}

int statsProc(uint16_t* p, unsigned int* pStats,int imgH, int imgW, int clip_h, int clip_w) {
	if (p == NULL) {
		std::cout << "source file open is null";
		return -1;
	}
	if (clip_w < 0) {
		std::cout << "block width size set error , out of range";
		return -1;
	}
	if (clip_h < 0) {
		std::cout << "block height size set error , out of range";
		return -1;
	}
	// default fovea area is center 1/4 area
	int top = imgH / 4;
	int left = imgW / 4;
	int sub_h = imgH / 2;
	int sub_w = imgW / 2;

	for (int y = clip_h; y + clip_h < imgH; y++) {
		int r = (y - clip_h);
		uint32_t index_add = y * imgW;
		int cnt = 0;
		uint32_t sum = 0;
		int sub_cnt = 0;
		uint32_t sub_sum_h = 0;
		for (int x = clip_w; x + clip_w < imgW; x++) {

			uint32_t pixel_index = index_add + x;
			uint32_t value = p[pixel_index] < 64 ? 0 : p[pixel_index] - 64;  // blc 
			value *= (1023.0 / 959);
			//cout << value << "	;";

			if ((y % 2 == 0 && x % 2 == 1) || (y % 2 == 1 && x % 2 == 0))  //rggb --> get g
			{
				//if (y>=top && y < top + sub_h && x >= left && x < left + sub_w)   //sub area row stats
				//{
				//	sub_sum_h += (value >> 2);
				//	sub_sum_w[(x-left) >> 1] += (vlaue >> 2);
				//	sub_cnt++;
				//}
				//else
				//{

				//}
				sum += (value / 4.0);    // 10 >> 8bit
				cnt++;
			}

		}
		pStats[r] = (int)(sum / cnt);
		
		//if (r %10 == 0)
		//{
		//	printf("gAvg: %d \n", pStats[r]);
		//}

	}
	return 0;
}

void getStats(string fileName, uint16_t* p, unsigned int* atf_stats, int imgH, int imgW, int woi_h, int woi_w)
{
	char file[512];
	char* file_name;
	strcpy(file, fileName.c_str());
	file_name = file;
	ReadRawFromFile(file_name, p);
	statsProc(p, atf_stats,imgH,imgW,woi_h,woi_w);
}

